import React from 'react';
import './Board.css'
import Square from './Square';
import s from './Square.css'

const setActive = ({isActive}) => isActive ? s.active : '';

const Board = ({squares, click}) => {
    return (
        <div className= {['board', {setActive}].join(' ')}>
    {squares.map((i, index) => (
        <Square
            value={i}
            onClick={() => click(index)}
        />
         ))}
        </div>
    )
            
}

export default Board;
